import { Component, signal } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { FormsModule } from '@angular/forms';
@Component({
  selector: 'app-root',
  imports: [RouterOutlet,FormsModule],
  templateUrl: './app.html',
  styleUrl: './app.css'
})
export class App {
  name:string=''
  url:string=''
  disc:string=''
  show(){
   let n:string=this.name.trim().toLowerCase()
  if(n=="doremon"){
    this.url="data:image/jpeg;base64,"
    this.disc="Hey, I am doremon, I have an idiot friend nobita, Who ends up in trouble all the time"
  }
  else if(n=="shinchan"){
    this.url="data:image/jpeg;base64,"
    this.disc="hey, na peru shinchan, nenu chala amayakudini, nenu ma chelle kanna chinnavadini"
  }
  else{
    this.url="data:image/jpeg;base64,"
    this.disc="either doremon ledha shinchan anthe, vallu edhare free ga unnaru"
  }
}
}
